// react hooks
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom"; 
import { UserAuthenication } from '../LoginContext';

import { auth, googleProvider } from "../config/firebase";


// Authentication Component
const Signup = () => {

    // setting states for email & password
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const navigate = useNavigate();
    const { createUser } = UserAuthenication();

    // createUser function,
    const signup = async (e) => {
        try {
            e.preventDefault(); // alternate way to await
            await createUser(email, password);
            navigate('/');
        } catch (e) {
            alert(e);
        }
    }


    return (
        <>
            <form className='max-w-[500px] justify-center content-center my-16 p-4 border p-2'>
                <p className='text-2xl font-bold py-2'> Are you a current user?
                    <Link to="/"> 
                    <span className="text-black-500 hover:text-sky-700" > Sign in</span>
                    </Link>
                </p>
                
                <div className='flex flex-col py-2'>
                    <label className='py-2 font-medium'>Email Address</label>
                    <input className="border p-2" 
                        type = "email"
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>

                <div className='flex flex-col py-2'>
                    <label className='py-2 font-medium'>Password</label>
                    <input className="border p-2"
                        type="password"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>


                <button className='border border-blue-500 bg-blue-600 hover:bg-blue-500 w-full p-4 my-2 text-white'
                onClick={signup}>Sign up</button>

                <div className="py-2">

                </div>
            </form>
        </>
    )
};

export default Signup;
