import React from 'react';
import axios from 'axios'
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import { auth } from "../config/firebase";
import { UserAuthenication } from '../LoginContext';

import {tailw} from "../config/styles";
import {navbar} from "../config/navbar";

axios.defaults.withCredentials = true;

const Account = () => {

  // handling user navigation
  const { user, logout } = UserAuthenication();
  const navigate = useNavigate();

  const handleLogout = async (e) => {
    try {
        e.preventDefault();  
        await logout(auth);
        navigate('/');
    } catch (e) {
        alert("hey logout issue");
    }
  }; 

  // handling display of data from backend
  const [datas, setDatas] = useState([]);

  useEffect(() => {
    fetch('api/display', {
      'method' : 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
  }).then(resp => resp.json())
  .then(resp => setDatas(resp))
  .catch(error => alert(error))
}, [])

  // handling POST of data to create venues via a form
  // create a default Venue input object
  const initialVenue = {
    name: "",
    location: "",
    closed: false,
    total_pax: 0,
    host: "",
  }
  const [values, setValues] = useState(initialVenue);

  // handle input change of the form
  const handleChange = (e) => {
    const {name, value} = e.target;

    setValues({
      ...values,
      [name]: value,
    })
    console.log(values)
  }

  // handle the submit of the form to django
  const handleSubmit = (e) => {
    //e.preventDefault();

    /*
    let formField = new FormData();
    formField.append('name',values.name)
    formField.append('location',values.location)
    formField.append('closed',values.closed)
    formField.append('total_pax',values.total_pax)
    formField.append('host',values.host)
    
    axios.post({
      method: 'post',
      url: `http://127.0.0.1:8000/api/venue`, 
      data: {
        formField
      }  
    })
      .then(res => {
        console.log(res);
        console.log(res.data);
      }); 
      

      
    fetch('http://127.0.0.1:8000/api/venue', {
      method: 'POST',
      headers: {"Content-Type":"application/json"},
      body: JSON.stringify(values)
    });
    */
    let formField = new FormData();
    formField.append('name',values.name)
    formField.append('location',values.location)
    formField.append('closed',values.closed)
    formField.append('total_pax',values.total_pax)
    formField.append('host',values.host)
    
    axios.post(`api/venue`, formField)
  };


  return (
    <div className='max-w-[600px] mx-auto my-16 p-4'>
      <nav class="bg-white dark:bg-gray-900 fixed w-full z-20 top-0 left-0 border-b border-gray-200 dark:border-gray-600">
        <div class="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
        <a href="https://flowbite.com/" class="flex items-center">
            <span class="self-center text-2xl font-semibold whitespace-nowrap dark:text-white">Flowbite</span>
        </a>
        <div class="flex md:order-2">
            <button type="button" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 text-center mr-3 md:mr-0 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Get started</button>
            <button data-collapse-toggle="navbar-sticky" type="button" class="inline-flex items-center p-2 text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600" aria-controls="navbar-sticky" aria-expanded="false">
                <span class="sr-only">Open main menu</span>
                <svg class="w-6 h-6" aria-hidden="true" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M3 5a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 10a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 15a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clip-rule="evenodd"></path></svg>
            </button>
        </div>
        <div class="items-center justify-between hidden w-full md:flex md:w-auto md:order-1" id="navbar-sticky">
            <ul class="flex flex-col p-4 md:p-0 mt-4 font-medium border border-gray-100 rounded-lg bg-gray-50 md:flex-row md:space-x-8 md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
            <li>
                <a href="/account" class="block py-2 pl-3 pr-4 text-white bg-blue-700 rounded md:bg-transparent md:text-blue-700 md:p-0 md:dark:text-blue-500" aria-current="page">Home</a>
            </li>
            <li>
                <a href="/account" class="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:hover:text-blue-700 md:p-0 md:dark:hover:text-blue-500 dark:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">About</a>
            </li>
            <li>
                <a href="/account" class="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:hover:text-blue-700 md:p-0 md:dark:hover:text-blue-500 dark:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">Services</a>
            </li>
            <li>
                <a href="/account" class="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:hover:text-blue-700 md:p-0 md:dark:hover:text-blue-500 dark:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">Contact</a>
            </li>
            </ul>
        </div>
        </div>
        </nav> 
      <h1 className='text-2xl font-bold py-4'>Account</h1>
      <p>Welcome User: {user?.email} </p>

      <button onClick={handleLogout} className='border px-6 py-2 my-4'>
        Logout
      </button>

      <div>
      {datas.map(datas => {
        return <h2>{datas.name}</h2>
      })}
      </div>

      <div>
        <form onSubmit={handleSubmit}>

          <input type="text" name="name" onChange={handleChange} 
          placeholder='name' value={values.name}/>
          <input type="text" name="location" onChange={handleChange} 
          placeholder='location' value={values.location}/>
          <input type="checkbox" name="closed" onChange={handleChange} 
          placeholder='closed' value={values.closed}/>
          <input type="number" name="total_pax" onChange={handleChange} 
          placeholder='total_pax' value={values.total_pax}/>
          <input type="text" name="host" onChange={handleChange} 
          placeholder='host' value={values.host}/>

          <button type="submit">Add</button>
        </form>

      </div>
      

    </div>
  );
};

export default Account;
