import axios from 'axios'
import { useState, useEffect } from 'react';

import { auth } from "../config/firebase";
import { UserAuthenication } from '../LoginContext';

export default function BookingDisplay() {
    // handling user navigation
    const { user, logout } = UserAuthenication();

    // remove the @... from the email 
    function extractNameFromEmail(email) {
        return email.split('@')[0];
    }
    const name = extractNameFromEmail(user?.email)

    // handling display of data from backend
    const [datas, setDatas] = useState([]);

    useEffect(() => {
        fetch('api/display', {
        'method' : 'GET',
        headers: {
            'Content-Type': 'application/json'
        }
    }).then(resp => resp.json())
    .then(resp => setDatas(resp))
    .catch(error => alert(error))
    }, [])

    return(      
        <div class="relative overflow-x-auto shadow-md sm:rounded-lg">
        <table class="w-full text-sm text-left text-gray-500 dark:text-gray-400">
            <caption class="p-5 text-lg font-semibold text-left text-gray-900 bg-white dark:text-white dark:bg-gray-800">
                {name}'s Bookings
                <p class="mt-1 text-sm font-normal text-gray-500 dark:text-gray-400">Browse a list display of your bookings and edit if needed</p>
            </caption>
            <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                <tr>
                    <th scope="col" class="px-6 py-3">
                        Product name
                    </th>
                    <th scope="col" class="px-6 py-3">
                        Color
                    </th>
                    <th scope="col" class="px-6 py-3">
                        Category
                    </th>
                    <th scope="col" class="px-6 py-3">
                        Price
                    </th>
                    <th scope="col" class="px-6 py-3">
                        <span class="sr-only">Edit</span>
                    </th>
                </tr>
            </thead>
            <tbody>

                {
                datas.map(data => {
                    return (
                    <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
                        <th scope="row" class="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                            Microsoft Surface Pro
                        </th>
                        <td class="px-6 py-4">
                            White
                        </td>
                        <td class="px-6 py-4">
                            Laptop PC
                        </td>
                        <td class="px-6 py-4">
                            $1999
                        </td>
                        <td class="px-6 py-4 text-right">
                            <a href="#" class="font-medium text-blue-600 dark:text-blue-500 hover:underline">Edit</a>
                        </td>
                    </tr>
                    )
                })
                }
                
            </tbody>
        </table>
        </div>

    )
}
