// react hooks
import { useState } from "react";
import { Link } from "react-router-dom";

import { auth, googleProvider } from "../config/firebase";
// importing methods from firebase authentication
import { createUserWithEmailAndPassword, 
signInWithPopup, signOut, signInWithEmailAndPassword} from "firebase/auth";

// Authentication Component
export const Signup = () => {

    // setting states for email & password
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    // createUser function 
    const signup = async (e) => {
        e.preventDefault();
        createUserWithEmailAndPassword(auth, email, password);
    };

    // LoginUser function 
    const login = async () => {
        alert("User logged in");
        await signInWithEmailAndPassword(auth, email, password);
    };

    // User Logout function
    const logout = async () => {
        alert("logged out");
        await signOut(auth);
    };

    return (
        <>
            <form className='max-w-[700px] mx-auto my-16 p-4 border p-2'>
                <p className='text-2xl font-bold py-2'> <Link to="/">Are you a current user? <span style='underline'>Sign in</span></Link></p>
                
                <div className='flex flex-col py-2'>
                <label className='py-2 font-medium'>Email Address</label>
                <input className="border p-2"
                    placeholder="Email..." 
                    onChange={(e) => setEmail(e.target.value)}
                />
                </div>

                <div className='flex flex-col py-2'>
                <label className='py-2 font-medium'>Password</label>
                <input className="border p-2"
                    placeholder="Password..." 
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                />
                </div>


                <button className='border border-blue-500 bg-blue-600 hover:bg-blue-500 w-full p-4 my-2 text-white'
                onClick={signup}>Sign up</button>

                <div className="py-2">
                <button onClick={login}>Login</button>

                <button onClick={logout}> logout </button>
                </div>
            </form>
        </>
    )
};
