import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

import { auth } from "../config/firebase";
// tailwind css object file
import tailw from "../config/styles";

import { UserAuthenication } from '../LoginContext';

const Signin = () => {
    // setting states for email & password
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const navigate = useNavigate();
    const { login } = UserAuthenication();

    //LoginUser function 
    const handleLogin = async (e) => {
        try {
            e.preventDefault();
            await login(email, password);
            navigate('/account');
        } catch (e) {
            alert(e);
            navigate('/signup');
        }
        
    }; 

    return (
        <div className={tailw.bg}>
        <div className="h-screen flex items-center justify-center bg-zinc-600 backdrop-opacity-40">
        <form className='max-w-[600px] mx-auto my-16 p-4 border p-2'>
            <p className={tailw.nav}> Are you a new user?
                <Link to="/signup"> 
                <span className="text-black-500 hover:text-sky-700" > Sign up</span>
                </Link>
            </p>

            <div className='flex flex-col py-2'>
                <label className='py-2 font-medium' for="email">Email Address</label>
                <input className="border p-2" type="email" name="email"
                    onChange={(e) => setEmail(e.target.value)}
                />
            </div>
            <div className='flex flex-col py-2'>
                <label className='py-2 font-medium' for="password">Password</label>
                <input className="border p-2" type="password" name="password"
                    onChange={(e) => setPassword(e.target.value)}
                />
            </div>

            <button className='border border-blue-500 bg-blue-600 hover:bg-blue-500 w-full p-4 my-2 text-white'
            onClick={handleLogin}>Sign in</button>
             
        </form>
        </div>
        </div>
    )
}

export default Signin;
