// react hooks
import { useState } from "react";

import { auth, googleProvider } from "../config/firebase";
// importing methods from firebase authentication
import { createUserWithEmailAndPassword, 
signInWithPopup, signOut, signInWithEmailAndPassword} from "firebase/auth";

import { Maindisplay } from "./maindisplay";
import {Link, Routes, Route, useNavigate, Router} from "react-router-dom";

// Authentication Component
export const Auth = () => {

    // navigate to main page once signed up
    const navigate = useNavigate();

    // setting states for email & password
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    // createUser function 
    const signup = async (e) => {
        e.preventDefault();
        createUserWithEmailAndPassword(auth, email, password);
        navigate("/main");
    };

    // LoginUser function 
    const login = async () => {
        alert("User logged in");
        await signInWithEmailAndPassword(auth, email, password);
    };

    // User Logout function
    const logout = async () => {
        alert("logged out");
        await signOut(auth);
    };

    return (
        <>
            <form>
                <input 
                    placeholder="Email..." 
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input 
                    placeholder="Password..." 
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                />

                <button onClick={signup}>Sign up</button>

                <button onClick={login}>Login</button>

                <button onClick={logout}> logout </button>
            </form>

            <Router>
                <Routes>
                    <Route path="/main" element={<Maindisplay/>} />
                </Routes>
            </Router>
        </>
    )
};

